// 이러한 문법은 안되는데, 이런 걸 가능하게 해주는게 웹펙(모듈 번들러)
// 나 안깔았는데? => create-react하면서 깔아졌음
// react-scripts라는 애가 create-react를 깔면서 같이 설치된걸 패키지 제이슨에서 볼 수 있는데 이 애가 webpack을 의존(디펜던시)한 상태라서 자동으로 같이 깔림
// node_modules 폴더안에 webpack이 있음
import "./App.css";
// 만약 불러오는 컴포넌트의 css명이 겹치면 늦게 수정된 css가 적용이 된다.
import Menu from "./components/menu/Menu";
import NewMenu from "./components/newMenu/NewMenu";

function App() {
  const name = "라이캐엣";
  const 변수 = "value";
  function 함수() {
    console.log("함수 함수!");
  }
  const 값 = true;

  return (
    <div className="App">
      {/* 사용자 정의 태그(만든거 불러옴) */}
      {/* <TextArea /> */}
      <Menu />
      <NewMenu />
      {/* 속성에 값이 문자열이면 중괄호 안써도 됨, 나머진 써야됨 */}
      <h3 hello="h1">안녕 {name} !!</h3>

      {/* true값을 갖는 속성은 속성 이름만으로 표현 가능 false라면 중괄호 사용 */}
      <input tyep="radio" checked></input>
      {/* class 속성은 calssName으로 표기 */}
      {/* 인라인 스타일은 객체형태로 사용 {{}} 중괄호 2개 */}
      <h2>hello world</h2>
      <h2>안녕 세상아</h2>

      {/* 자바스크립트 문법을 react jsx안에서 사용할 수 있다. */}
      {100 + 1}
      {"hello" + "world"}

      {/* map 같은 경우 ,(쉼표) 제거후 별개의 문자열로 반환함 */}
      {[1, 2, 3].map((x) => x ** 2)}
      {함수()}
      {변수}
      {/* 삼항식도 됨 */}
      {값 ? "one" : "two"}

      <ToDay />
    </div>
  );
}

// function TextArea() {
//   return (
//     <div className="wrapper">
//       {/* 속성 값이 문자열이 아니면 중괄호{} 인라인 내용은 객체 */}
//       <textarea readOnly maxLength={3} style={{ backgroundColor: "blue" }} />
//       {/* 속성들은 다 카멜케이스로 해야함 */}
//     </div>
//   );
// }

function ToDay() {
  const today = new Date();
  const year = today.getFullYear();
  const month = today.getMonth() + 1;
  const day = today.getDay();
  const hour = today.getHours();
  const minute = today.getMinutes();
  const second = today.getSeconds();

  const yearStyle = { background: "black", color: "white" };

  return (
    <div style={{ textAlign: "start", fontWeight: "bold" }}>
      <h2 style={{ backGroundColor: "black", color: "white" }}>
        안녕, 라이캣! 1호
      </h2>
      <h2>안녕 라이캣 2호!</h2>
      <div style={yearStyle}>
        <h2 style={{ color: "red" }}>{"년: " + year}</h2>
        <h2>{"월/일: " + month + "/" + day}</h2>
        <h2>{"시간: " + hour + "시 " + minute + "분 " + second + "초"}</h2>
      </div>
    </div>
  );
}

export default App;
